import React from 'react';
import { Form, Popover } from 'antd';
import InputItem from '../../components/InputItem';
import styles from './index.module.less';

const Register = () => {
    const [form] = Form.useForm();
    const handleFinish = (values) => {
        console.log(values);
    }
    const checkConfirm = (_, value) => {
        const promise = Promise;
        if (value && value !== form.getFieldValue('password')) {
            return promise.reject('passwords do not match!');
        }
        return promise.resolve();
    }

    const checkPassword = (_, value)  => {
        const promise = Promise;
        if (!value) {
            return promise.reject('please input password')
        }
        if (value && form.getFieldValue('confirm')) {
            form.validateFields(['confirm']);
        }
        return promise.resolve();
    }

    const renderPasswordProgress = () => {
        const value = form.getFieldValue('password');
        return value && value.length && (
            <div>
                <Progress
                    strokeWidth={6}
                    percent
                />
            </div>
        )
    }

    return (
        <div className={styles.registerContainer}>
            <div className={styles.register}>
                <Form
                    form={form}
                    onFinish={handleFinish}
                >
                    <InputItem
                        name="mail"
                        placeholder="mailbox"
                        size="large"
                        rules={[
                            {
                                required: true,
                                message: "Please input mail!"
                            },
                            {
                                type: 'email',
                                message: 'Please input mail with right format!'
                            },
                        ]}
                    />
                    <Popover
                        content={
                            <div>
                                {renderPasswordProgress()}
                                <div>
                                    Please use at least 6 characters. Please avoid easy passwords.
                                </div>
                            </div>
                        }
                        overlayStyle={{ width: 240}}
                        placement="right"
                        visible={true}

                    >
                        <InputItem
                            name="password"
                            type="password"
                            placeholder="at least 6 characters"
                            size="large"
                            rules={[
                                {
                                    validator: checkPassword,
                                }
                            ]}
                        />
                    </Popover>
                    <InputItem
                        name="confirm"
                        type="password"
                        placeholder="confirm password"
                        size="large"
                        rules={[
                            {
                                required: true,
                                message: "Please confirm password"
                            },
                            {
                                validator: checkConfirm,
                            }
                        ]}
                    />
                </Form>
            </div>
        </div>
    )
};

export default Register;
