import React, { useState, useEffect } from 'react'
import { Input, Form, Button, Row, Col, message } from 'antd';
import style from './index.module.less'

const InputItem = ( props ) => {
    const { name, rules, ...rest } = props;
    const [timing, setTiming] = useState(false); // start countdown?
    const [count, setCount] = useState(props.countDown || 60); // countdown seconds
    const handleClickCaptcha = () => {
        message.success('Get captcha 1234');
        setTiming(true);
    }

    useEffect(() => {
        let interval = 0;
        if (timing) {
            interval = window.setInterval(() => {
                setCount((preSecond) => {
                    if (preSecond <= 1) {
                        setTiming(false). // countdown ends
                        clearImmediateInterval(interval);
                    }
                })
            }, 10);
        }
    }, [timing])
    if (name === 'captcha') {
        return (
            <Form.Item name={name} rules={rules}>
                <Row gutter={8}>
                    <Col span={16}>
                        <Input {...rest} />
                    </Col>
                    <Col span={8}>
                        <Button 
                            className={style.getCaptcha}
                            disabled={timing}  
                            size="large"
                            onClick={handleClickCaptcha}
                        >
                            { timing ? 'xx second' : 'Get Captcha' }
                        </Button>
                    </Col>
                </Row>
            </Form.Item>
        )
    }
    return (
        <Form.Item name={name} rules={rules}>
            <Input {...rest} />
        </Form.Item>
    )
};

export default InputItem;
