import React from 'react';
import { List, Card, Tooltip, Dropdown, Menu, Avatar } from 'antd';
import { DownloadOutlined, EditOutlined, ShareAltOutlined, EllipsisOutlined } from '@ant-design/icons';
import numeral from 'numeral';
import styles from './index.module.less';

const itemMenu = (
    <Menu>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com">
                1st menu Item
            </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com">
                2nd menu Item
            </a>
        </Menu.Item>
        <Menu.Item>
            <a target="_blank" rel="noopener noreferrer" href="http://www.alipay.com">
                3rd menu Item
            </a>
        </Menu.Item>
    </Menu>
)

const CardInfo = ({ activeUser, newUser }) => (
    <div className={styles.cardInfo}>
        <div>
            <p>Active User</p>
            <p>{activeUser}</p>
        </div>
        <div>
            <p>New User</p>
            <p>{newUser}</p>
        </div>
    </div>
);

function formatWan(val) {
    const v = val * 1;
    if (!v || Number.isNaN(v)) return ''
    let result = val;
    if (val > 10000) {
        result = (
            <span>
                {Math.floor(val / 10000)}
                <span>
                    ten thousand
                </span>
            </span>
        )
    }
    return result;
}

const Applications = ({ list }) => {
    return (
        <List
            className={styles.filterCardList}
            rowKey="id"
            grid={{ gutter: 24, xxl: 3, xl: 2, lg: 2, md: 2, sm: 2, xs: 1 }}
            dataSource={list}
            renderItem={(item) => (
                <List.Item
                    key={item.id}
                >
                    <Card
                        hoverable
                        bodyStyle={{ paddingBottom: 20 }}
                        actions={[
                            <Tooltip key="download" title="download">
                                <DownloadOutlined />
                            </Tooltip>,
                            <Tooltip key="edit" title="edit">
                                <EditOutlined />
                            </Tooltip>,
                            <Tooltip key="share" title="share">
                                <ShareAltOutlined />
                            </Tooltip>,
                            <Dropdown overlay={itemMenu} key="ellipsis">
                                <ShareAltOutlined />
                            </Dropdown>,
                        ]}
                    >
                        <Card.Meta 
                            avatar={<Avatar size="small" src={item.avatar} />}
                            title={item.title}
                        />
                        <div>
                            <CardInfo
                                activeUser={formatWan(item.activeUser)}
                                newUser={numeral(item.newUser).format('0,0')}
                            />
                        </div> 
                    </Card>
                </List.Item>
            )}
        />
    );
};

export default Applications;
